<?php
class ControllerModuleMainPageSeo extends Controller {	public function index() {   		$this->load->language('module/mainpageseo');		$this->document->setTitle($this->language->get('heading_title'));
		$this->load->model('setting/setting');						if (($this->request->server['REQUEST_METHOD'] == 'POST')) {			$this->model_setting_setting->editSetting('mainpageseo', $this->request->post);					$this->session->data['success'] = 'Налаштування модуля успішно змінено';			$this->redirect($this->url->link('extension/module', 'token=' . $this->session->data['token'], 'SSL'));		}		
		$this->data['heading_title'] = $this->language->get('heading_title');
		$this->data['text_enabled'] = $this->language->get('text_enabled');		$this->data['text_disabled'] = $this->language->get('text_disabled');		$this->data['text_content_top'] = $this->language->get('text_content_top');		$this->data['text_content_bottom'] = $this->language->get('text_content_bottom');				$this->data['text_column_left'] = $this->language->get('text_column_left');		$this->data['text_column_right'] = $this->language->get('text_column_right');				$this->data['entry_code'] = $this->language->get('entry_code');		$this->data['entry_layout'] = $this->language->get('entry_layout');		$this->data['entry_position'] = $this->language->get('entry_position');		$this->data['entry_status'] = $this->language->get('entry_status');		$this->data['entry_sort_order'] = $this->language->get('entry_sort_order');				$this->data['button_save'] = $this->language->get('button_save');		$this->data['button_cancel'] = $this->language->get('button_cancel');		$this->data['button_add_module'] = $this->language->get('button_add_module');		$this->data['button_remove'] = $this->language->get('button_remove');		  		$this->data['breadcrumbs'] = array();   		$this->data['breadcrumbs'][] = array(       		'text'      => $this->language->get('text_home'),			'href'      => $this->url->link('common/home', 'token=' . $this->session->data['token'], 'SSL'),      		'separator' => false   		);   		$this->data['breadcrumbs'][] = array(       		'text'      => $this->language->get('text_module'),			'href'      => $this->url->link('extension/module', 'token=' . $this->session->data['token'], 'SSL'),      		'separator' => ' :: '   		);		   		$this->data['breadcrumbs'][] = array(       		'text'      => $this->language->get('heading_title'),			'href'      => $this->url->link('module/facebook', 'token=' . $this->session->data['token'], 'SSL'),      		'separator' => ' :: '   		);				$this->data['action'] = $this->url->link('module/mainpageseo', 'token=' . $this->session->data['token'], 'SSL');		
		$this->data['cancel'] = $this->url->link('extension/module', 'token=' . $this->session->data['token'], 'SSL');
		if (isset($this->request->post['uk_title'])) {			$this->data['uk_title'] = $this->request->post['uk_title'];		} else {			$this->data['uk_title'] = $this->config->get('uk_title');		}			if (isset($this->request->post['uk_desc'])) {			$this->data['uk_desc'] = $this->request->post['uk_desc'];		} else {			$this->data['uk_desc'] = $this->config->get('uk_desc');		}								$this->template = 'module/mainpageseo.tpl';		$this->children = array(			'common/header',			'common/footer',		);					$this->response->setOutput($this->render());	}
}?>